type Mutation {
  generate_personal_access_token(input: CreateNewAuthTokenInput): CreateNewAuthTokenOutput
		@aws_auth(cognito_groups: ["NormalGroup","AdminGroup","Engineers","Automation","Testing","FieldOperator","Intervention","Maintenance","NormalGroup","Operators","Optimizer","SafetyCritical"])
}

input CreateNewAuthTokenInput {
  Username: String!
  TokenName: String!
  operator: String!
}

type CreateNewAuthTokenOutput {
  TokenID: String
  time: String
  Username: String
  AccessToken: String
  TokenName: String
}


type Query {
  get_personal_access_token(input: GetAuthTokensInput): GetAuthTokensOutput
}

input GetAuthTokensInput {
  Username: String!
  operator: String!
}

type GetAuthTokensOutput {
  TokenData: [AWSJSON]
}


schema {
	query: Query
    mutation: Mutation
}
